def get_command_group_and_arg(command):
    cmd_list = command.split(';')
    cmd = cmd_list[0]
    arg = cmd_list[1] if len(cmd_list) > 1 else None
    group = "".join(map(lambda k: k if cmd in eval(k) else '', COMMAND_GROUPS))

    return group, arg
